data.widget_css:
/* CSS for Frontpage Title*/
               #threeblmediawidget h2.threebl_title {color:#797878;font-size:22px; margin-top: 10px;text-transform: uppercase;}

                /* CSS for Frontpage Headline */
                #threeblmediawidget ul li h2 a.threebl_fmr_headlines{color:#231F20;font-size:22px;margin: 0;padding: 0;
                vertical-align: middle;line-height:26px;text-decoration:none}
                #threeblmediawidget ul li h2 a.threebl_fmr_headlines:hover{color:#231F20;font-size:22px;margin: 0;padding: 0;vertical-align: middle; line-height:26px;}

                /* CSS for Date and Time */
                #threeblmediawidget .threebl_datetime {color:#797878 !important; font-size:13px !important; padding: 5px 0; display:;}

                /* CSS for View more link */
                #threeblmediawidget ul li a.threebl_vew_more{color:#797878;font-size:px;}
                #threeblmediawidget ul li a.threebl_vew_more:hover {color:#ED1C24;font-size:px ;}

                /* CSS for Headline blocks*/
                #threeblmediawidget ul {list-style:none;}
                #threeblmediawidget ul.threebl_sidebar{ list-style:none; padding:0 10px; font-family:"Droid Serif", Geneva, Arial, Helvetica, sans-serif;}
                #threeblmediawidget ul.threebl_sidebar li{ padding:10px 0; border-bottom:1px dotted #ccc}
                #threeblmediawidget ul.threebl_sidebar li.threebl_border-none{ border:none}{ padding:10px 0; border-bottom:1px dotted #fff;}

                /* CSS for anchor tag*/
                #threeblmediawidget ul.threebl_sidebar a{text-decoration:none; display:block; color:#00AACC;}
                #threeblmediawidget ul.threebl_sidebar a:hover{ color:#00AACC; text-decoration:none;}

                /* CSS for Read more link */
                #threeblmediawidget ul.threebl_sidebar li .threebl_view-more{ text-align:right; font-size:11px;}

                /* CSS for Powered By 3BLMedia.com */
                #threeblmediawidget .threebl_link a {color:#444444 !important;font-size:13px; text-decoration:none}
                #threeblmediawidget ul.threebl_sidebar a.threebl_read_more{display: inline;}

                /* css for front page thumbnail width*/
                #threeblmediawidget ul.threebl_sidebar div.thumbnailWidth{width: 16% !important;display: inline-block !important;box-sizing: border-box !important;
vertical-align: top;}
                                    #threeblmediawidget ul.threebl_sidebar div.thumbnailWidth img{width:100% !important}
                                    #threeblmediawidget ul.threebl_sidebar div.rightdivwidth{width: calc(80% - 10px) !important;display: inline-block !important;}

                 /* css for front page teaser*/
                #threeblmediawidget ul.threebl_sidebar div.threebl_frontdesc{color:#A0A0A0;width: calc(80% - 10px) !important;line-height: 16px;display: inline-block !important;font-size :13px}

                /* Common classes */
                #threeblmediawidget {color:#333; font-family:"Droid Serif", Geneva, Arial, Helvetica, sans-serif;font-size:12px;padding: 0 20px; }
                #threeblmediawidget h1, #threeblmediawidget h2, #threeblmediawidget h3{ padding:0; margin:0; font-family:"Droid Serif", Geneva, Arial, Helvetica, sans-serif }
                #threeblmediawidget a:link, #threeblmediawidget a:visited, #threeblmediawidget a:hover, #threeblmediawidget a:active {color:#00AACC;}#threeblmediawidget ul.threebl_sidebar a {color:#666666;}
